import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';  //permet d'être rediriger sur une route
import '../style/components/_login.scss';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const history = useHistory();

 const addToList = () => {
  axios.post('http://localhost:3002/login', {email: email, password: password });
  };

  return (
    <div className="container-login">
      <h1>Connexion</h1>
      <form className="form-login" >
        <input type="text" value={email} placeholder="Email" onChange={(e) => setEmail(e.target.value)} />
        <input type="password" value={password} placeholder="Password" onChange={(e) => setPassword(e.target.value)} />
        <button onClick={addToList} type="submit">Se Connecter</button>
      </form>
    </div>
  );
}

export default Login;
