import React, { useState,useEffect } from 'react';
import axios from 'axios';
import '../style/components/_connexion.scss';

function Connexion() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const[usersList,setUsersList]=useState([]);
  const[newEmail,setNewEmail]=useState('');

  useEffect(() => {
    axios.get('http://localhost:3001/membres').then((response) => {
      setUsersList(response.data);
    });

  }, []);

 const addToList = () => {
  axios.post('http://localhost:3001/connexion', {email: email, password: password });
  };

  const updateEmail= (id) => {
    axios.put("http://localhost:3001/maj", {
      id:id,
      newEmail:newEmail,
    });
  };

  return (
    <div className="container-login">
      <h1>Connexion</h1>
      <form className="form-login" >
        <input type="text" value={email} placeholder="Email" onChange={(e) => setEmail(e.target.value)} />
        <input type="password" value={password} placeholder="Password" onChange={(e) => setPassword(e.target.value)} />
        <button onClick={addToList} type="submit">Se Connecter</button>
      </form>
      <h1 >Liste des Membres :</h1>
      {usersList.map((val,key) => {
        return (
          <div key={key}>
            <h4> {val.firstname}{" "}{val.lastname} </h4>
            <input type="text" placeholder="Modifier le mail..."
            onChange={(event) => {
              setNewEmail(event.target.value);
            }}/>
            <button onClick={() => updateEmail(val._id)}>Update</button>
            <button>Delete</button>
          </div> 
        );
      })}
    </div>
  );
}

export default Connexion;
