import React, {useEffect, useState} from 'react';
import Scroll from '../components/Scroll';
import Logo from '../components/Logo';
import Navigation from '../components/Navigation';
import '../style/pages/_structure.scss'


const Structure = () => {

    const [clicked, setClicked] = useState(false);
    
        useEffect(() => {
            if(clicked) {
        window.location.href = "https://poker-clock.herokuapp.com/";
            }
      });

    return (
        <main>
            <div className="widget">
                <Navigation />
                <Logo />
                <div className="row">
                    <div className="column">
                        <img className="images" src="https://code.iconify.design/2/2.1.0/iconify.min.js" alt="jeton1"></img>
                        <h3 align="center">Valeur1</h3>
                    </div>
                    <div className="column">
                    <img className="images" src="https://code.iconify.design/2/2.1.0/iconify.min.js" alt="jeton2"></img>
                        <h3 align="center">Valeur2</h3>
                    </div>
                    <div className="column">
                        <img className="images" src="https://code.iconify.design/2/2.1.0/iconify.min.js" alt="jeton3"></img>
                        <h3 align="center">Valeur3</h3>
                    </div>
                    <div className="column">
                        <img className="images" src="https://code.iconify.design/2/2.1.0/iconify.min.js" alt="jeton4"></img>
                        <h3 align="center">Valeur4</h3>
                    </div>
                </div>
                <div className="button-container-struct">
                    <button className="button" onClick={() => setClicked(true)}> Cr√©er sa propre Structure ! </button>
                </div>
                <Scroll left={'/planning'} right={'/sondages'} />
            </div>
        </main>
    );
};

export default Structure;
