import React, {useEffect, useState} from 'react';
import Scroll from '../components/Scroll';
import Logo from '../components/Logo';
import Navigation from '../components/Navigation';
import '../style/pages/_structure.scss'
import { Icon } from '@iconify/react';


const Structure = () => {

    const [clicked, setClicked] = useState(false);
    
        useEffect(() => {
            if(clicked) {
        window.location.href = "https://poker-clock.herokuapp.com/";
            }
      });

    function myFunctionalComponentFunction() {
      const [valeur, setValeur] = useState('');

    return (
        <main>
            <div className="widget">
                <Navigation />
                <Logo />
                <div className="row">
                    <div className="column">
                    <Icon className="jeton" icon="mdi:poker-chip" color="blue" />
                        <h3 align="center">Valeur : 10</h3>
                    </div>
                    <div className="column">
                    <Icon className="jeton" icon="mdi:poker-chip" color="green" />
                        <h3 align="center">Valeur : 50</h3>
                    </div>
                    <div className="column">
                    <Icon className="jeton" icon="mdi:poker-chip" color="red" />
                    <input type="number" value="valeur" onInput={e => setValeur(e.target.value)}/>
                        <h3 align="center">Valeur : 100</h3>
                    </div>
                    <div className="column">
                    <Icon className="jeton" icon="mdi:poker-chip" color="black" />
                        <h3 align="center">Valeur : 500</h3>
                    </div>
                </div>
                <div className="button-container-struct">
                    <button className="button" onClick={() => setClicked(true)}> Créer sa propre Structure ! </button>
                </div>
                <Scroll left={'/planning'} right={'/sondages'} />
            </div>
        </main>
    );
    }
};

export default Structure;
