import React from 'react';
const google = window.google;

const MapBase = () => {
    // Location de l'ECE Paris
    const ece = { lat: 48.852024, lng: 2.28666 };
    // La map centrée sur l'ECE
    const map = new google.maps.Map(document.getElementById("map"), {
      zoom: 15,
      center: ece,
    });
    // Le marker positionné sur l'ECE
    const marker = new google.maps.Marker({
      position: ece,
      map: map,
    });
    
    return (
        <div id="map">
            <script 
            src="https://maps.googleapis.com/maps/api/js?key=AIzaSyCIc-9Qw-Ime5dg4ADN5kCpgY-hbPkBwKQ&callback=initMap&libraries=&v=weekly"
            async
            ></script> 
        </div>
    );
};

export default MapBase;
